mapper.select:
SELECT t.id, t.username, t.`password`, t.status, t.real_name, t.mobile, t.creation_time, ws.`name` AS store_name FROM sys_user t LEFT JOIN sys_user_store sur ON sur.user_id = t.id LEFT JOIN wkl_store ws ON ws.id = sur.store_id WHERE 1=1
AND ( t.username LIKE CONCAT('%',#{text},'%') OR t.real_name LIKE CONCAT('%',#{text},'%') OR t.mobile LIKE CONCAT('%',#{text},'%') OR ws.name LIKE CONCAT('%',#{text},'%') )
AND t.status = #{status}

SELECT DISTINCT sr.permission FROM sys_resource sr LEFT JOIN sys_role_resource srr ON srr.resource_id = sr.id LEFT JOIN sys_role sro ON sro.id = srr.role_id LEFT JOIN sys_user_role sur ON sur.role_id = sro.id LEFT JOIN sys_user su ON su.id = sur.user_id WHERE sr.permission IS NOT NULL AND sr.permission <> '' AND su.id = #{userId}
UNION
SELECT srp.perm as permission FROM sys_role_perm srp LEFT JOIN sys_user_role sur ON sur.role_id = srp.role_id WHERE sur.user_id = #{userId}
